(()=>{"use strict";const e=new class{constructor(e,t){this.user=e,this.score=t}scoresData=[];apiURL="https://us-central1-js-capstone-backend.cloudfunctions.net/api/games/Ff2lEvzHFw6jYxy6okeP/scores/";showScores=()=>{document.getElementById("list").innerHTML=this.scoresData.map((e=>`\n    <li>${e.user} : ${e.score}</li>`)).join("")};fetchScores=async()=>{try{const e=await fetch(this.apiURL),t=await e.json();return this.scoresData=[],t.result.map((e=>this.scoresData.push(e))),this.showScores()}catch(e){return e}};addNewScore=async({user:e,score:t})=>{try{const s={method:"POST",headers:{Accept:"application/json","Content-Type":"application/json"},body:JSON.stringify({user:e,score:t})},c=await fetch(this.apiURL,s),r=await c.json();return this.scoresData.push(r),this.fetchScores()}catch(e){return e}}},t=document.querySelector(".add-form");t.addEventListener("submit",(s=>{s.preventDefault();const c=t.name.value,r=t.score.value;e.addNewScore({user:c,score:r}),t.reset()})),document.getElementById("refresh").addEventListener("click",e.fetchScores),document.addEventListener("DOMContentLoaded",e.showScores)})();